// Función auxiliar para obtener versículos de un libro y capítulo
function obtenerVersiculos(nombreLibro, numeroCapitulo) {
    if (BIBLIA_RV1960[nombreLibro] && BIBLIA_RV1960[nombreLibro][numeroCapitulo]) {
        return BIBLIA_RV1960[nombreLibro][numeroCapitulo];
    }
    return null;
}

// Función auxiliar para obtener información de un libro
function obtenerInfoLibro(nombreLibro) {
    return LIBROS_INFO.find(libro => libro.nombre === nombreLibro);
}

// Función auxiliar para obtener todos los libros
function obtenerTodosLibros() {
    return LIBROS_INFO;
}
